  ///      that is allowed to be utilized to be exactly 2300 when receiving ethereum.
  ///
  /// @param amount The amount of tokens to burn.
  function withdraw(uint256 amount) external;
}","keccak256":"0x6df1a375b5b39f03ee997b1509f296c9ff3e66a96afbbe6c5924d5b4ac74db31"},"submodules/v2-foundry/src/interfaces/external/balancer/IAsset.sol":{"content":"// SPDX-License-Identifier: GPL-3.0-or-later
